import React, { useState } from 'react';
import './AddPlayerDialog.css';

const AddPlayerDialog = ({ isOpen, onClose, onAddPlayer }) => {
  const [form, setForm] = useState({ name: '', amount: '' });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.name || !form.amount || Number(form.amount) <= 0) return;
    onAddPlayer(form);
    setForm({ name: '', amount: '' });
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="dialog-overlay">
      <div className="dialog-content">
        <h3>Add New Player</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="playerName">Player Name</label>
            <input
              id="playerName"
              type="text"
              value={form.name}
              onChange={(e) => setForm({ ...form, name: e.target.value })}
              placeholder="Enter player name"
              autoFocus
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="buyInAmount">Buy-In Amount</label>
            <input
              id="buyInAmount"
              type="number"
              min="0"
              step="0.01"
              value={form.amount}
              onChange={(e) => setForm({ ...form, amount: e.target.value })}
              placeholder="Enter buy-in amount"
              required
            />
          </div>
          <div className="dialog-actions">
            <button type="button" className="cancel-btn" onClick={onClose}>
              Cancel
            </button>
            <button 
              type="submit" 
              className="submit-btn"
              disabled={!form.name || !form.amount || Number(form.amount) <= 0}
            >
              Add Player
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddPlayerDialog;
