import { useEffect, useState, useContext } from 'react';
import ThemeToggle from '../components/ThemeToggle';
import { useNavigate } from 'react-router-dom';
import { listGames, createGame, clearToken, deleteGame, renameGame } from '../api';
import { GameContext } from '../context/GameContext';

const GamesList = () => {
  const [games, setGames] = useState([]);
  const navigate = useNavigate();
  const { setGameId } = useContext(GameContext);

  useEffect(() => {
    (async () => {
      try {
        const res = await listGames();
        setGames(res.data.games);
      } catch (e) {
        console.error(e);
        setGames([]);
      }
    })();
  }, []);

  const open = (id) => {
    setGameId(id);
    navigate(`/game/${id}`);
  };

  const logout = () => {
    clearToken();
    window.location.href = '/login';
  };

  const handleCreate = async () => {
    const currency = prompt('Currency code (e.g., USD, INR):', 'USD') || 'USD';
    const date = new Date().toISOString().slice(0,10);
    const playersStr = prompt('Initial player names (comma separated, optional):', '');
    const players = playersStr ? playersStr.split(',').map(s => s.trim()).filter(Boolean) : [];
    const res = await createGame({ currency, date, players });
    setGames([...games, { id: res.data.gameId, title: res.data.title }]);
    open(res.data.gameId);
  };

  return (
    <div className="container">
      <div className="brand">ChipMate</div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop:'0.5rem' }}>
        <h2>Your Games</h2>
        <ThemeToggle />
      </div>
      <button onClick={handleCreate}>Create New Game</button>
      <button onClick={logout} style={{ marginLeft: '1rem' }}>Logout</button>
      <ul style={{listStyle:'none', padding:0}}>
        {games.map((g) => (
          <li key={g.id} className="card-item">
            <span style={{ flex:1, cursor:'pointer' }} onDoubleClick={async ()=>{
              const n = prompt('Rename game', g.title);
              if(n){ await renameGame(g.id, n); setGames(games.map(o=>o.id===g.id?{...o,title:n}:o)); }
            }}>{g.title}</span>
            <button onClick={() => open(g.id)}>Open</button>
            <button onClick={async () => { if(window.confirm('Delete this game?')){ await deleteGame(g.id); setGames(games.filter(o=>o.id!==g.id)); } }}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default GamesList;
