import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getPlayerStats } from '../api';
import { useToast } from '../context/ToastContext';
import ThemeToggle from '../components/ThemeToggle';

const PlayerStats = () => {
  const [players, setPlayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [sortBy, setSortBy] = useState('net'); // 'net', 'games', 'buyIns'
  const navigate = useNavigate();
  const { showToast } = useToast();

  useEffect(() => {
    fetchPlayerStats();
  }, []);

  const fetchPlayerStats = async () => {
    try {
      setLoading(true);
      const response = await getPlayerStats();
      setPlayers(response.data.players);
      setError('');
    } catch (err) {
      console.error('Error fetching player stats:', err);
      setError(err.errorInfo?.message || 'Failed to load player stats');
      showToast('Failed to load player stats', 'error');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const getSortedPlayers = () => {
    const sorted = [...players];
    switch (sortBy) {
      case 'net':
        return sorted.sort((a, b) => b.net - a.net);
      case 'games':
        return sorted.sort((a, b) => b.gamesPlayed - a.gamesPlayed);
      case 'buyIns':
        return sorted.sort((a, b) => b.totalBuyIns - a.totalBuyIns);
      default:
        return sorted;
    }
  };

  const getSortLabel = (field) => {
    switch (field) {
      case 'net': return 'Net Profit/Loss';
      case 'games': return 'Games Played';
      case 'buyIns': return 'Total Buy-Ins';
      default: return field;
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="brand">ChipMate</div>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '0.5rem' }}>
          <button onClick={() => navigate('/')}>&larr; Home</button>
          <h2>Player Stats</h2>
          <ThemeToggle />
        </div>
        <div style={{ textAlign: 'center', padding: '2rem' }}>
          Loading player stats...
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="brand">ChipMate</div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '0.5rem' }}>
        <button onClick={() => navigate('/')}>&larr; Home</button>
        <h2>Player Stats</h2>
        <ThemeToggle />
      </div>

      {error && (
        <div style={{
          backgroundColor: '#ffebee',
          color: '#c62828',
          padding: '1rem',
          borderRadius: '4px',
          marginBottom: '1rem'
        }}>
          {error}
        </div>
      )}

      {players.length === 0 ? (
        <div style={{ textAlign: 'center', padding: '2rem' }}>
          <p>No players found. Start playing some games to see player stats!</p>
          <button onClick={() => navigate('/')} style={{ marginTop: '1rem' }}>
            Create Your First Game
          </button>
        </div>
      ) : (
        <div>
          <div style={{ marginBottom: '1rem', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <label htmlFor="sortBy" style={{ marginRight: '0.5rem' }}>Sort by:</label>
              <select
                id="sortBy"
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                style={{
                  padding: '0.5rem',
                  borderRadius: '4px',
                  border: '1px solid var(--border-color, #ccc)',
                  backgroundColor: 'var(--bg-color, white)',
                  color: 'var(--text-primary, #333)'
                }}
              >
                <option value="net">Net Profit/Loss</option>
                <option value="games">Games Played</option>
                <option value="buyIns">Total Buy-Ins</option>
              </select>
            </div>
            <button onClick={fetchPlayerStats}>
              Refresh Stats
            </button>
          </div>

          <div style={{ display: 'grid', gap: '0.5rem' }}>
            {getSortedPlayers().map((player, index) => (
              <div
                key={player.name}
                style={{
                  padding: '1rem',
                  border: '1px solid var(--border-color, #ccc)',
                  borderRadius: '8px',
                  backgroundColor: 'var(--bg-color, white)'
                }}
              >
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '0.5rem' }}>
                  <div>
                    <h3 style={{ margin: 0, fontSize: '1.2rem' }}>{player.name}</h3>
                    <div style={{ fontSize: '0.9rem', opacity: 0.7 }}>
                      {player.gamesPlayed} game{player.gamesPlayed !== 1 ? 's' : ''} played
                    </div>
                  </div>
                  <div style={{ textAlign: 'right' }}>
                    <div style={{
                      fontWeight: 'bold',
                      fontSize: '1.1rem',
                      color: player.net >= 0 ? '#4caf50' : '#f44336'
                    }}>
                      {formatCurrency(player.net)}
                    </div>
                    <div style={{ fontSize: '0.8rem', opacity: 0.7 }}>
                      Net Profit/Loss
                    </div>
                  </div>
                </div>

                <div style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
                  gap: '1rem',
                  marginTop: '1rem',
                  padding: '1rem',
                  backgroundColor: 'var(--bg-color, white)',
                  borderRadius: '4px',
                  border: '1px solid var(--border-color, #eee)'
                }}>
                  <div>
                    <div style={{ fontWeight: 'bold', color: '#2196f3' }}>
                      {formatCurrency(player.totalBuyIns)}
                    </div>
                    <div style={{ fontSize: '0.8rem', opacity: 0.7 }}>
                      Total Buy-Ins
                    </div>
                  </div>
                  <div>
                    <div style={{ fontWeight: 'bold', color: '#ff9800' }}>
                      {formatCurrency(player.totalCashOut)}
                    </div>
                    <div style={{ fontSize: '0.8rem', opacity: 0.7 }}>
                      Total Cash-Outs
                    </div>
                  </div>
                  <div>
                    <div style={{ fontWeight: 'bold', color: '#9c27b0' }}>
                      {player.gamesPlayed}
                    </div>
                    <div style={{ fontSize: '0.8rem', opacity: 0.7 }}>
                      Games Played
                    </div>
                  </div>
                  <div>
                    <div style={{ fontWeight: 'bold', color: '#607d8b' }}>
                      {formatCurrency(player.totalBuyIns / player.gamesPlayed)}
                    </div>
                    <div style={{ fontSize: '0.8rem', opacity: 0.7 }}>
                      Avg Buy-In/Game
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div style={{ marginTop: '2rem', textAlign: 'center' }}>
            <div style={{
              padding: '1rem',
              backgroundColor: 'var(--bg-color, white)',
              border: '1px solid var(--border-color, #ccc)',
              borderRadius: '8px',
              display: 'inline-block'
            }}>
              <h4 style={{ margin: '0 0 0.5rem 0' }}>Summary</h4>
              <div style={{ fontSize: '0.9rem', opacity: 0.8 }}>
                {players.length} total players â€¢ {players.reduce((sum, p) => sum + p.gamesPlayed, 0)} total games
              </div>
