import React, { useState } from 'react';
import { useSwipeable } from 'react-swipeable';
import './PlayerCard.css';

const PlayerCard = ({
  name,
  totalBuyIn,
  cashOut,
  net,
  onEditBuyIn,
  onEditCashOut,
  onDelete,
  formatter
}) => {
  const handleActionClick = (e, action) => {
    e.stopPropagation();
    action();
  };

  return (
    <div className="player-card">
      <div className="player-card-content">
        <div className="player-info">
          <div className="player-name">{name}</div>
          <div className="player-amounts">
            <span className="net-amount" style={{ color: net >= 0 ? '#4caf50' : '#f44336' }}>
              {formatter.format(net)}
            </span>
            <span className="buyin-amount">
              {formatter.format(totalBuyIn)} in ‚Ä¢ {formatter.format(cashOut || 0)} out
            </span>
          </div>
        </div>

        <div className="player-actions">
          <button 
            className="action-btn edit-btn" 
            onClick={(e) => handleActionClick(e, onEditBuyIn)}
            aria-label="Edit player"
          >
            ‚úèÔ∏è
          </button>
          <button 
            className="action-btn delete-btn"
            onClick={(e) => handleActionClick(e, onDelete)}
            aria-label="Delete player"
          >
            üóëÔ∏è
          </button>
        </div>
      </div>
    </div>
  );
};

export default PlayerCard;
