import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getLeaderboard } from '../api';
import { useToast } from '../context/ToastContext';
import ThemeToggle from '../components/ThemeToggle';

const Leaderboard = () => {
  const [leaderboard, setLeaderboard] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { showToast } = useToast();

  useEffect(() => {
    fetchLeaderboard();
  }, []);

  const fetchLeaderboard = async () => {
    try {
      setLoading(true);
      const response = await getLeaderboard();
      setLeaderboard(response.data.leaderboard);
      setError('');
    } catch (err) {
      console.error('Error fetching leaderboard:', err);
      setError(err.errorInfo?.message || 'Failed to load leaderboard');
      showToast('Failed to load leaderboard', 'error');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const getRankColor = (rank) => {
    if (rank === 1) return '#FFD700'; // Gold
    if (rank === 2) return '#C0C0C0'; // Silver
    if (rank === 3) return '#CD7F32'; // Bronze
    return 'var(--text-primary, #333)';
  };

  if (loading) {
    return (
      <div className="container">
        <div className="brand">ChipMate</div>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '0.5rem' }}>
          <button onClick={() => navigate('/')}>&larr; Home</button>
          <h2>Leaderboard</h2>
          <ThemeToggle />
        </div>
        <div style={{ textAlign: 'center', padding: '2rem' }}>
          Loading leaderboard...
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="brand">ChipMate</div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '0.5rem' }}>
        <button onClick={() => navigate('/')}>&larr; Home</button>
        <h2>Leaderboard</h2>
        <ThemeToggle />
      </div>

      {error && (
        <div style={{
          backgroundColor: '#ffebee',
          color: '#c62828',
          padding: '1rem',
          borderRadius: '4px',
          marginBottom: '1rem'
        }}>
          {error}
        </div>
      )}

      {leaderboard.length === 0 ? (
        <div style={{ textAlign: 'center', padding: '2rem' }}>
          <p>No players found. Start playing some games to see the leaderboard!</p>
          <button onClick={() => navigate('/')} style={{ marginTop: '1rem' }}>
            Create Your First Game
          </button>
        </div>
      ) : (
        <div>
          <div style={{ marginBottom: '1rem', textAlign: 'center' }}>
            <p style={{ opacity: 0.7, fontSize: '0.9rem' }}>
              Ranked by net profit/loss across all games
            </p>
          </div>

          <div style={{ display: 'grid', gap: '0.5rem' }}>
            {leaderboard.map((player, index) => (
              <div
                key={player.name}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  padding: '1rem',
                  border: '1px solid var(--border-color, #ccc)',
                  borderRadius: '8px',
                  backgroundColor: 'var(--bg-color, white)',
                  gap: '1rem'
                }}
              >
                <div style={{
                  width: '2rem',
                  height: '2rem',
                  borderRadius: '50%',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontWeight: 'bold',
                  color: getRankColor(index + 1),
                  fontSize: '1.2rem'
                }}>
                  {index + 1}
                </div>

                <div style={{ flex: 1 }}>
                  <div style={{ fontWeight: 'bold', fontSize: '1.1rem' }}>
                    {player.name}
                  </div>
                  <div style={{ fontSize: '0.9rem', opacity: 0.7 }}>
                    {player.gamesPlayed} game{player.gamesPlayed !== 1 ? 's' : ''} played
                  </div>
                </div>

                <div style={{ textAlign: 'right' }}>
                  <div style={{
                    fontWeight: 'bold',
                    fontSize: '1.1rem',
                    color: player.net >= 0 ? '#4caf50' : '#f44336'
                  }}>
                    {formatCurrency(player.net)}
                  </div>
                  <div style={{ fontSize: '0.8rem', opacity: 0.7 }}>
                    {formatCurrency(player.totalBuyIns)} in • {formatCurrency(player.totalCashOut)} out
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div style={{ marginTop: '2rem', textAlign: 'center' }}>
            <button onClick={fetchLeaderboard}>
              Refresh Leaderboard
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Leaderboard;
